function agreement1() {
  var showme = document.getElementById("agreement1");

  if (showme.style['visibility'] == 'hidden') {
    showme.style.visibility = "visible";
    showme.style.display = "inline";
  } else {
    showme.style.visibility = "hidden";
    showme.style.display = "none";
  }
}

