$("#register-now").click(function(){
    $("#splash").hide();
    $("#banners-1").hide();
    $("#banners-2").hide();
    $(".center-banner").hide();
    $("#registration-page").fadeIn();
});

$("#registration-banner").click(function(){
    window.scrollTo(0, 0);
    $("#splash").hide();
    $("#banners-1").hide();
    $("#banners-2").hide();
    $(".center-banner").hide();
    $("#registration-page").fadeIn();
});

